
public class Test9 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x[]= {1,2,3,4,5};
		for(int i=0;i<x.length;i++)
		{
			System.out.println("original array: "+x[i]+" ");
		}
		
		for(int i=x.length-1;i>=0;i--)
		{
			System.out.println("riversed array: "+x[i]+" ");
		}

	}

}
